int main ( ) { int t , a [ max ] , n , i , j ; scanf ( "%d" , & t ) ; while ( t > 0 ) { int count = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { for ( j = 0 ; j <= 1 ; j ++ ) { if ( a [ i ] <= a [ i + j ] ) { count ++ ; } } } printf ( "%d" , count ) ; t -- ; } return 0 ; }