int main ( ) { FILE * p , * q , * w ; int count = 0 , count1 = 0 , i ; char a ; p = fopen ( "d.c" , "r" ) ; q = fopen ( "dd.c" , "r" ) ; w = fopen ( "ddd.c" , "w" ) ; while ( 1 ) { a = fgetc ( p ) ; if ( a != EOF ) { if ( a == '\n' ) count ++ ; } else break ; } while ( 1 ) { a = fgetc ( q ) ; if ( a == '\n' ) count1 ++ ; else if ( a == EOF ) break ; } fclose ( p ) ; fclose ( q ) ; printf ( "%d" , count ) ; p = fopen ( "d.c" , "r" ) ; q = fopen ( "dd.c" , "r" ) ; for ( i = 0 ; i < count ; i ++ ) { while ( ( ( a = fgetc ( p ) ) != '\n' ) && ( a != EOF ) ) fprintf ( w , "%c" , a ) ; fprintf ( w , "\n" ) ; while ( ( ( a = fgetc ( q ) ) != '\n' ) && ( a != EOF ) ) fprintf ( w , "%c" , a ) ; fprintf ( w , "\n" ) ; } fclose ( p ) ; fclose ( q ) ; fclose ( w ) ; }