int main ( ) { int T , N ; int * A ; int cost , i , j ; scanf ( "%d" , & T ) ; while ( T > 0 ) { cost = 0 ; scanf ( "%d" , & N ) ; A = ( int * ) malloc ( N * sizeof ( int ) ) ; for ( i = 0 ; i < N ; i ++ ) { scanf ( "%d" , & A [ i ] ) ; } i = 0 ; j = 1 ; while ( j < N ) { if ( A [ i ] < A [ j ] ) { cost += A [ i ] ; } else { cost += A [ j ] ; i = j ; } j ++ ; } printf ( "%d\n" , cost ) ; T -- ; } return 0 ; }