int main ( ) { int array [ SIZE ] ; int n ; int col ; scanf ( "%d" , & n ) ; int i , j , k , index ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & col ) ; index = 0 ; for ( j = 0 ; j < col ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & array [ index ++ ] ) ; } } int j_init , j_upper_init , j_upper , row_size ; j_init = j = ( col * ( col + 1 ) ) / 2 - col ; row_size = col - 1 ; j_upper_init = j_upper = j_init - row_size ; while ( row_size >= 0 ) { for ( k = 0 ; k < row_size ; k ++ ) { array [ j_upper ] += array [ j ] > array [ j + 1 ] ? array [ j ] : array [ j + 1 ] ; j ++ ; j_upper ++ ; } row_size -- ; j_upper_init = j_upper = j_upper_init - row_size ; j_init = j = j_init - row_size - 1 ; } printf ( "%d\n" , array [ 0 ] ) ; } return 0 ; }