int main ( ) { int t , n1 , n2 , num , deno , rem , lcm , gcd ; scanf ( "%d\n" , & t ) ; while ( t -- ) { scanf ( "%d\n" , & n1 ) ; scanf ( "%d\n" , & n2 ) ; if ( n1 > n2 ) { num = n1 ; deno = n2 ; } else { num = n2 ; deno = n1 ; } rem = n1 % n2 ; while ( rem != 0 ) { num = deno ; deno = rem ; rem = n1 % n2 ; } gcd = deno ; lcm = n1 * n2 / gcd ; printf ( "%d%d\n" , gcd , lcm ) ; } return 0 ; }