int main ( ) { long int n1 , n2 , i , gcd , lcm , t ; scanf ( "%d" , & t ) ; while ( t > 0 ) { printf ( "Enter two positive integers: " ) ; scanf ( "%d %d" , & n1 , & n2 ) ; for ( i = 1 ; i <= n1 && i <= n2 ; ++ i ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } lcm = ( n1 * n2 ) / gcd ; printf ( "%d\n%d\n" , gcd , lcm ) ; t -- ; } return 0 ; }