int main ( void ) { int i , j , n , t , count , count1 , temp ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] ; count1 = count = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { if ( count1 == 0 ) { temp = a [ i ] ; count1 ++ ; } if ( a [ i ] >= temp ) { count1 ++ ; temp = a [ i ] ; } else if ( a [ i ] < temp ) { count += count1 * ( count1 + 1 ) / 2 ; count1 = 0 ; } } if ( count == 0 ) count = 1 ; printf ( "%d\n" , count ) ; } return 0 ; }