main ( ) { int T , a , j , i , sum = 0 ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d" , & a ) ; int arr [ a ] ; for ( j = 0 ; j < a ; j ++ ) { scanf ( "%d" , & arr [ j ] ) ; } for ( j = 1 ; j < a ; j ++ ) { if ( arr [ 0 ] > arr [ j ] ) { sum = sum + arr [ j ] ; arr [ 0 ] = arr [ j ] ; } if ( arr [ 0 ] < arr [ j ] ) { sum = sum + arr [ 0 ] ; } } printf ( "%d\n" , sum ) ; sum = 0 ; } }