int main ( ) { static int a [ 100 ] [ 100 ] ; int k = 0 , sum , t , n , i , j ; scanf ( "%d" , & t ) ; while ( t != 0 ) { sum = 0 ; if ( k != 0 ) { for ( i = 0 ; i <= k - 1 ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) a [ i ] [ j ] = 0 ; } } i = 0 ; scanf ( "%d" , & n ) ; k = n ; while ( i <= n - 1 ) { j = 0 ; while ( j <= i ) { scanf ( "%d" , & a [ i ] [ j ] ) ; j ++ ; } i ++ ; } sum = a [ 0 ] [ 0 ] ; i = 0 , j = 0 ; while ( i < n - 1 && j < n - 1 ) { if ( a [ i + 1 ] [ j ] > a [ i + 1 ] [ j + 1 ] ) { sum = sum + a [ i + 1 ] [ j ] ; i ++ ; } else { sum = sum + a [ i + 1 ] [ j + 1 ] ; i ++ ; j ++ ; } } printf ( "%d" , sum ) ; t -- ; } return 0 ; }