int GetGCDLCM ( int , int ) ; int main ( ) { int x , y , GCD , LCM ; printf ( "Enter Two Numbers:\n" ) ; scanf ( "%d\t %d" , & x , & y ) ; GCD = GetGCDLCM ( x , y ) ; LCM = ( ( x * y ) / GCD ) ; printf ( "GCD & LCM of two numbers is %d\t %d" , GCD , LCM ) ; return 0 ; } int GetGCDLCM ( int n1 , int n2 ) { int i , gcd ; for ( i = 1 ; i <= n1 && i <= n2 ; ++ i ) { if ( n1 % i == 0 && n2 % i == 0 ) gcd = i ; } return gcd ; }