int main ( ) { int t , a , b , i , lcm , gcd , j , c , d ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; lcm = 1 ; gcd = 1 ; if ( a == b ) printf ( "%d %d" , b , b ) ; else { if ( a > b ) { c = a ; d = b ; } else if ( a < b ) { c = b ; d = a ; } for ( j = 1 ; j <= d ; j ++ ) { if ( ( c % j == 0 ) && ( d % j == 0 ) ) { lcm = lcm * j ; gcd = gcd * j ; c = c / j ; d = d / j ; } } if ( ( d > 0 ) || ( c > 0 ) ) lcm = lcm * d * c ; printf ( "%d %d\n" , gcd , lcm ) ; } } return 0 ; }