int main ( ) { long long int a , b , n1 , n2 ; int t ; do { scanf ( "%d" , & t ) ; } while ( t < 1 || t > 1000 ) ; int lcm ; while ( t > 0 ) { b = 0 ; do { scanf ( "%ld" , & a ) ; } while ( a < 1 && a > 1000000 ) ; do { scanf ( "%ld" , & b ) ; } while ( b < 1 && b > 1000000 ) ; n1 = a ; n2 = b ; while ( n1 != n2 ) { if ( n1 > n2 ) n1 = n1 - n2 ; else n2 = n2 - n1 ; } printf ( "%ld " , n1 ) ; lcm = a * b / n1 ; printf ( "%ld\n" , lcm ) ; t -- ; } }