int main ( ) { long long int t , n , k , i ; long long int count ; scanf ( "%lld" , & t ) ; while ( t -- ) { scanf ( "%lld" , & n ) ; int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; i = 1 , k = 1 , count = 0 ; while ( i < n ) { if ( arr [ i - 1 ] <= arr [ i ] ) k ++ ; else { count += k * ( k + 1 ) / 2 ; k = 1 ; } i ++ ; } count += k * ( k + 1 ) / 2 ; printf ( "%lld\n" , count ) ; } return 0 ; }