int main ( ) { int a , b , i , sum , t , arr [ 50000 ] ; scanf ( "%d" , & a ) ; while ( a -- ) { sum = 0 ; scanf ( "%d" , & b ) ; for ( i = 0 ; i < b ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } for ( i = 0 ; i < b - 1 ; i ++ ) { if ( arr [ i ] > arr [ i + 1 ] ) { sum = sum + arr [ i + 1 ] ; } else { sum = sum + arr [ i ] ; t = arr [ i ] ; arr [ i ] = arr [ i + 1 ] ; arr [ i + 1 ] = t ; } } printf ( "%d\n" , sum ) ; } return 0 ; }