int main ( ) { int t ; unsigned int num [ 50000 ] , min , n , temp , i ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & num [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( num [ i ] < num [ i + 1 ] ) { temp = num [ i ] ; num [ i ] = num [ i + 1 ] ; num [ i + 1 ] = temp ; min += temp ; } } printf ( "\n%d" , min ) ; min = 0 ; } return 0 ; }