int lines , inp [ 200 ] [ 200 ] ; int func ( int i , int j ) { if ( i == lines ) return inp [ i ] [ j ] ; else { int left_sum , right_sum ; left_sum = func ( i + 1 , j ) ; right_sum = func ( i + 1 , j + 1 ) ; if ( left_sum > right_sum ) return left_sum + inp [ i ] [ j ] ; else return right_sum + inp [ i ] [ j ] ; } } int main ( ) { int i , iter , test_cases , j ; scanf ( "%d" , & test_cases ) ; int ans [ test_cases ] ; for ( iter = 0 ; iter < test_cases ; iter ++ ) { scanf ( "%d" , & lines ) ; for ( i = 1 ; i <= lines ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & inp [ i ] [ j ] ) ; } } ans [ iter ] = func ( 1 , 1 ) ; } for ( i = 0 ; i < test_cases ; i ++ ) printf ( "%d\n" , ans [ i ] ) ; return 0 ; }