int gcd_lcm ( long int a , long int b ) { long int t1 , t2 , t3 ; t1 = a ; t2 = b ; do { t3 = t1 % t2 ; t1 = t2 ; t2 = t3 ; } while ( t2 != 0 ) ; printf ( "\n%d %d" , t1 , ( a * b ) / t1 ) ; } int main ( ) { int t , i ; long int a , b , t1 , t2 , t3 ; scanf ( "%d" , & t ) ; if ( 1 <= t <= 1000 ) { for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( 1 <= a <= 1000000 && 1 <= b <= 1000000 ) { gcd_lcm ( a , b ) ; } } } return 0 ; }