int main ( ) { int t , n , i , j , k , temp ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & a [ i ] ) ; for ( j = 0 ; j < n ; j ++ ) for ( k = j ; k < n ; k ++ ) { if ( a [ j ] > a [ k ] ) { temp = a [ j ] ; a [ j ] = a [ k ] ; a [ k ] = temp ; } } int count = 0 , m = n ; for ( j = 0 ; j < n ; j ++ ) { for ( k = j ; k < m ; k ++ ) { if ( a [ j ] <= a [ k + 1 ] ) count ++ ; } m -- ; } printf ( "%d\n" , count ) ; } return 0 ; }