unsigned long long int getGCD ( unsigned long long int A , unsigned long long int B ) ; int main ( ) { int T ; unsigned long long int A , B , n ; scanf ( "%d" , & T ) ; while ( T -- ) { scanf ( "%llu %llu" , & A , & B ) ; n = getGCD ( A , B ) ; printf ( "%llu %llu\n" , n , ( A * B ) / n ) ; } return 0 ; } unsigned long long int getGCD ( unsigned long long int A , unsigned long long int B ) { unsigned long int max , min , t , s ; if ( A > B ) { max = A ; min = B ; } else { max = B ; min = A ; } if ( max % min == 0 ) { return min ; } else { while ( 1 ) { t = max % min ; if ( t == 0 ) break ; s = min ; min = t ; max = s ; } return min ; } }