int main ( ) { int cas , sum , row , l , m , i , j , k ; scanf ( "%d" , & cas ) ; for ( k = 1 ; k <= cas ; k ++ ) { scanf ( "%d" , & row ) ; int a [ row ] [ row ] ; for ( i = 1 ; i <= row ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } m = 1 ; sum = a [ 1 ] [ 1 ] ; for ( i = 2 ; i <= row ; i ++ ) { if ( a [ i ] [ m ] > a [ i ] [ m + 1 ] ) sum += a [ i ] [ m ] ; else { sum += a [ i ] [ m + 1 ] ; m ++ ; } } printf ( "%d\n" , sum ) ; } return 0 ; }