int computetriangle ( int nRows ) { int i , j , k ; int arr2 [ 100 ] [ 100 ] ; int sum1 , sum2 ; for ( i = 0 ; i <= nRows - 1 ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & k ) ; arr2 [ i ] [ j ] = k ; } } for ( i = nRows - 1 ; i >= 1 ; i -- ) { for ( j = i ; j >= 1 ; j -- ) { sum2 = arr2 [ i ] [ j ] + arr2 [ i - 1 ] [ j - 1 ] ; sum1 = arr2 [ i ] [ j - 1 ] + arr2 [ i - 1 ] [ j - 1 ] ; if ( sum2 > sum1 ) { arr2 [ i - 1 ] [ j - 1 ] = sum2 ; } if ( sum1 >= sum2 ) { arr2 [ i - 1 ] [ j - 1 ] = sum1 ; } } } if ( sum2 > sum1 ) { printf ( "%d\n" , sum2 ) ; } else { printf ( "%d\n" , sum1 ) ; } } int main ( void ) { int nTestCases , i , nRows ; scanf ( "%d" , & nTestCases ) ; for ( i = 0 ; i < nTestCases ; i ++ ) { scanf ( "%d" , & nRows ) ; computetriangle ( nRows ) ; } return 0 ; }