int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { int a , b , i = 1 ; int gcd , lcm ; scanf ( "%d %d" , & a , & b ) ; if ( a < b ) { for ( i = a ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; break ; } } } else { for ( i = b ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { gcd = i ; } } } lcm = ( ( a * b ) / gcd ) ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }