int main ( ) { int t , pos , level , i , ele , j ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%d" , & level ) ; ele = ( level * ( level + 1 ) ) / 2 ; int n [ ele ] ; for ( i = 0 ; i < ele ; i ++ ) { scanf ( "%d" , & n [ i ] ) ; } j = 1 ; pos = 0 ; ele = n [ 0 ] ; for ( i = 1 ; i < level ; i ++ ) { if ( n [ pos + i ] > n [ pos + i + 1 ] ) { ele = ele + n [ pos + i ] ; pos = pos + i ; } else { ele = ele + n [ pos + i + 1 ] ; pos = pos + i + 1 ; } } printf ( "%d\n" , ele ) ; t -- ; } return 0 ; }