long int gcd ( long int a , long int b ) { int temp = 0 ; if ( a < b ) { temp = a ; a = b ; b = temp ; } if ( b == 0 ) { return a ; } else return ( gcd ( b , a % b ) ) ; } int main ( ) { int tc = 0 ; long int a , b , temp = 0 ; scanf ( "%d" , & tc ) ; while ( tc -- ) { scanf ( "%ld %ld" , & a , & b ) ; temp = a * b ; printf ( "%ld %ld\n" , gcd ( a , b ) , temp / gcd ( a , b ) ) ; } return 0 ; }