int main ( ) { int k , res [ 10000000 ] , i , a , b , t , s , temp , m = 0 ; scanf ( "%d" , k ) ; printf ( "/n" ) ; for ( i = 0 ; i ++ < k ; ) { scanf ( "%d%d" , & a , & b ) ; printf ( "/n" ) ; if ( a < b ) { temp = a ; a = b ; b = temp ; } t = a ; s = b ; while ( b != 0 ) { temp = a ; a = b ; b = temp % a ; } res [ m ] = a ; res [ m + 1 ] = ( s * t ) / a ; m = m + 2 ; } for ( i = 0 ; i < ( sizeof ( res ) / sizeof ( res [ 0 ] ) ) ; i = i + 2 ) { printf ( "%d\t%d" , res [ i ] , res [ i + 1 ] ) ; } return 0 ; }