int main ( ) { int a , b , t , gcd , lcm , m , n , p , q ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; m = a ; n = b ; if ( a == b ) { gcd = a ; lcm = b ; } else if ( a > b ) { if ( a % b == 0 ) { gcd = b ; lcm = a ; } else { p = 1 ; while ( p ) { q = a / b ; p = a - ( q * b ) ; a = b ; gcd = b ; b = p ; } lcm = ( m * n ) / gcd ; } } else { if ( b % a == 0 ) { gcd = a ; lcm = b ; } else { p = 1 ; while ( p ) { q = b / a ; p = b - ( q * a ) ; b = a ; gcd = a ; a = p ; } lcm = ( m * n ) / gcd ; } } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }