int main ( ) { int t , i = 1 , num , den , rem , lcm , gcd ; int a [ 1001 ] ; int b [ 1001 ] ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d %d" , & a [ i ] , & b [ i ] ) ; if ( a [ i ] > b [ i ] ) { num = a [ i ] ; den = b [ i ] ; } else { num = b [ i ] ; den = a [ i ] ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = a [ i ] * b [ i ] / gcd ; printf ( "%d %d" , gcd , lcm ) ; } }