int gcd ( int a , int b ) { while ( b % a != 0 ) { int r = b % a ; b = a ; a = r ; } printf ( "%d " , a ) ; } int lcm ( int a , int b ) { int f ; for ( int i = b ; i <= a * b ; i ++ ) if ( i % a == 0 && i % b == 0 ) f = i ; printf ( "%d\n" , f ) ; } main ( ) { int a , b , t ; scanf ( "%d" , & t ) ; for ( int i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { a = a + b ; b = a - b ; a = a - b ; } gcd ( a , b ) ; lcm ( a , b ) ; } }