int main ( ) { int t , n , i , min , sum = 0 ; scanf ( "%d" , & t ) ; while ( t ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } min = a [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { if ( min > a [ i ] ) min = a [ i ] ; } sum = min * ( n - 1 ) ; printf ( "%d\n" , sum ) ; sum = 0 ; t -- ; } }