int main ( ) { int t , n , j , k , x ; scanf ( "%d" , & t ) ; int a [ t ] ; int i ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int b [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & b [ j ] ) ; for ( j = 0 ; j < 1 ; j ++ ) { for ( k = j + 1 ; k < n ; k ++ ) { if ( b [ k ] < b [ j ] ) { x = b [ k ] ; b [ k ] = b [ j ] ; b [ j ] = x ; } } } a [ i ] = ( n - 1 ) * x ; } for ( i = 0 ; i < t ; i ++ ) printf ( "%d\n" , a [ i ] ) ; return 0 ; }