int main ( ) { int T , l , i , n , j , sum , k = 1 , x ; int arr [ 100 ] ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; ++ i ) { k = 1 ; x = 2 ; scanf ( "%d" , & l ) ; n = ( l * ( l + 1 ) ) / 2 ; for ( j = 0 ; j < n ; ++ j ) { scanf ( "%d" , & arr [ j ] ) ; } sum = arr [ 0 ] ; while ( k <= n ) { if ( arr [ k ] > arr [ k + 1 ] ) { sum = sum + arr [ k ] ; k = k + x ; } else { sum = sum + arr [ k + 1 ] ; k = k + 1 + x ; } ++ x ; } printf ( "%d\n" , sum ) ; } return 0 ; }