int main ( ) { int t , a = 0 ; scanf ( "%d" , & t ) ; long int e , f ; long int gcd [ t ] , lcm [ t ] ; while ( a < t ) { int i = 1 ; scanf ( "%ld %ld" , & e , & f ) ; long int j = ( e > f ) ? f : e ; long int z = e * f ; while ( i <= j ) { if ( e % i == 0 && f % i == 0 ) { gcd [ a ] = i ; } ++ i ; } lcm [ a ] = z / gcd [ a ] ; printf ( "%ld %ld\n" , gcd [ a ] , lcm [ a ] ) ; a ++ ; } return 0 ; }