int main ( ) { int a , b , t , temp1 , dividend , divisor , gcd , i , rem = 1 ; long long int temp , lcm ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; temp = a * b ; if ( a < b ) { temp1 = a ; a = b ; b = temp1 ; } dividend = a ; divisor = b ; while ( rem > 0 ) { rem = dividend % divisor ; dividend = divisor ; if ( rem == 0 ) break ; divisor = rem ; } gcd = divisor ; lcm = temp / gcd ; printf ( "%d %lld\n" , gcd , lcm ) ; rem = 1 ; } return 0 ; }