int main ( ) { int tc , n , i , j , k , c ; scanf ( "%d" , & tc ) ; while ( tc -- ) { c = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = i + 1 ; j < n ; j ++ ) { if ( a [ i ] < a [ j ] ) { for ( k = j ; k < n ; k ++ ) a [ k ] = a [ k + 1 ] ; c = c + a [ i ] ; } else { for ( k = i ; k < n ; k ++ ) a [ k ] = a [ k + 1 ] ; c = c + a [ j ] ; } n -- ; } } printf ( "%d" , c ) ; } }