int main ( ) { int t , i , j , r , k , l , sum1 , sum2 , sum ; int a [ 100 ] [ 100 ] ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & r ) ; for ( j = 0 ; j < r ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & a [ j ] [ k ] ) ; } } sum = a [ 0 ] [ 0 ] ; j = 0 ; k = 0 ; while ( j < r ) { sum1 = 0 ; sum2 = 0 ; for ( l = j + 1 ; l < r ; l ++ ) sum1 += a [ l ] [ k ] ; for ( l = j + 1 ; l < r ; l ++ ) sum2 += a [ l ] [ k + 1 ] ; if ( sum1 > sum2 ) { sum += a [ j + 1 ] [ k ] ; j ++ ; } else if ( sum1 < sum2 ) { sum += a [ j + 1 ] [ k + 1 ] ; j ++ ; k ++ ; } else if ( sum1 == sum2 ) { if ( a [ j + 1 ] [ k ] > a [ j + 1 ] [ k + 1 ] ) { sum += a [ j + 1 ] [ k ] ; j ++ ; } else { sum += a [ j + 1 ] [ k + 1 ] ; j ++ ; k ++ ; } } } printf ( "%d\n" , sum ) ; } return 0 ; }