int main ( ) { int testcase ; int input ; int b ; int m , n ; scanf ( "%d\n" , & testcase ) ; while ( testcase -- ) { scanf ( "%d\n" , & input ) ; int * cell [ input ] ; for ( m = 0 ; m < input ; m ++ ) { int cell [ input ] [ m ] ; for ( n = 0 ; n <= m ; n ++ ) { scanf ( "%d" , & b ) ; cell [ m ] [ n ] = b ; } } for ( m = input - 2 ; m >= 0 ; m -- ) { for ( n = m ; n >= 0 ; n -- ) { cell [ m ] [ n ] = cell [ m ] [ n ] + max ( cell [ m + 1 ] [ n ] , cell [ m + 1 ] [ n + 1 ] ) ; } } int ans = cell [ 0 ] [ 0 ] ; printf ( "%d\n" , ans ) ; } return 0 ; }