int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t > 0 ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; int min ; if ( a > b ) { min = b ; } else { min = a ; } int num ; for ( int i = 1 ; i <= min ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { num = i ; } } int sum1 = a ; int sum2 = b ; while ( 1 ) { if ( sum1 == sum2 ) { break ; } else if ( sum1 > sum2 ) { sum2 = sum2 + b ; } else if ( sum2 > sum1 ) { sum1 = sum1 + a ; } } printf ( "%d %d\n" , num , sum1 ) ; t -- ; } return 0 ; }