int main ( ) { int t ; long int n , i , j ; long long int a [ 50000 ] , min , k ; scanf ( "%d\n" , & t ) ; while ( t -- ) { { scanf ( "%ld\n" , & n ) ; } for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld " , & a [ i ] ) ; } min = 0 ; j = n ; while ( -- n ) { if ( min > a [ n ] ) { k = min ; min = a [ n ] ; a [ n ] = k ; } } printf ( "\n%lld" , ( j * min - min ) ) ; } return 0 ; }