int main ( ) { int i , T , a , b , j , c , smal , larg , d ; scanf ( "%d" , & T ) ; for ( i = 1 ; i <= T ; i ++ ) { c = 1 ; scanf ( "%d%d" , & a , & b ) ; smal = a ; larg = b ; if ( b < a ) { smal = b ; larg = a ; } for ( j = 2 ; j <= smal ; j ++ ) { if ( a % j == 0 && b % j == 0 ) c = j ; } printf ( "%d\t" , c ) ; c = larg ; d = 1 ; while ( c <= a * b ) { if ( c % a == 0 && c % b == 0 ) { d = c ; break ; } c ++ ; } printf ( "%d\n" , d ) ; } return 0 ; }