int main ( ) { int t ; int a , b , gcd , keep , temp ; int small , big , remainder ; long long int product , lcm ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%d%d" , & a , & b ) ; product = ( a * b ) ; small = ( a < b ) ? a : b ; big = ( a > b ) ? a : b ; if ( big % small == 0 ) gcd = small ; else { remainder = ( big % small ) ; keep = small ; while ( remainder != 0 ) { temp = remainder ; remainder = keep % remainder ; keep = temp ; } gcd = keep ; } lcm = product / ( long long int ) gcd ; printf ( "%d %lld\n" , gcd , lcm ) ; t -- ; } return 0 ; }