void main ( ) { int t , a , b , h , l , g , i ; scanf ( "%d" , & t ) ; while ( t != 0 ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; for ( i = a ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { l = i ; break ; } } if ( a < b ) h = a ; else h = b ; for ( i = h ; i <= a * b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { g = i ; break ; } } printf ( "%d %d" , l , g ) ; t -- ; } }