int main ( ) { int A , B , T , i , t , z , y ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d%d" , & A , & B ) ; z = A * B ; if ( A > B ) { do { t = A % B ; A = B ; B = t ; } while ( B != 0 ) ; } else if ( B > A ) { do { t = B % A ; B = A ; A = t ; } while ( A != 0 ) ; } if ( A > B ) { y = z / A ; printf ( "%d %d" , A , y ) ; } else if ( B > A ) { y = z / B ; printf ( "%d %d\n" , B , y ) ; } } }