int main ( ) { unsigned int T , j , c ; T = j = c = 0 ; scanf ( "%u" , & T ) ; for ( j = 0 ; j < T ; j ++ ) { long int N , i ; long long int A , B , op , c ; N = A = B = op = c = i = 0 ; scanf ( "%ld" , & N ) ; scanf ( "%lld %lld" , & A , & B ) ; if ( A >= B ) { c = B ; op += B ; } else { c = A ; op += A ; } for ( i = 2 ; i < N ; i ++ ) { A = 0 ; scanf ( "%lld" , & A ) ; if ( c >= A ) { c = A ; op += A ; } else { op += c ; } } printf ( "%lld\n" , op ) ; } return 0 ; }