int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int num1 , num2 , gcd , lcm , numerator , denominator , remainder ; scanf ( "%lld%lld" , & num1 , & num2 ) ; if ( num1 > num2 ) { numerator = num1 ; denominator = num2 ; } else { numerator = num2 ; denominator = num1 ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = ( num1 * num2 ) / gcd ; printf ( "%lld%lld/n" , gcd , lcm ) ; } }