int main ( ) { int t , a , b , i , gcd , min ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; if ( a <= b ) min = a ; else min = b ; for ( i = 1 ; i <= min ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; } printf ( "%d %d\n" , gcd , ( ( a * b ) / gcd ) ) ; } return 0 ; }