int main ( ) { int a , b , i , j , T ; scanf ( "%d" , & T ) ; int * k = ( int * ) malloc ( T * sizeof ( int ) ) ; int * l = ( int * ) malloc ( T * sizeof ( int ) ) ; for ( i = 1 ; i <= T ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; int x = a , y = b ; while ( b != 0 ) { j = b ; b = a % b ; a = j ; } k [ i - 1 ] = a ; l [ i - 1 ] = x * y / k [ i - 1 ] ; } for ( i = 0 ; i < T ; i ++ ) printf ( "%d %d\n" , k [ i ] , l [ i ] ) ; return 0 ; }