int main ( ) { int t ; scanf ( "%d" , & t ) ; int n , i ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } int j = a [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) if ( j > a [ i ] ) j = a [ i ] ; printf ( "%d\n" , j * ( n - 1 ) ) ; } return 0 ; }