int main ( ) { int i , j , k , t , n , a [ 100 ] , p ; long int sum ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; scanf ( "%d" , a [ 0 ] ) ; sum = a [ 0 ] ; p = 0 ; for ( j = 1 ; j < n ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & a [ k ] ) ; } if ( a [ p ] > a [ p + 1 ] ) sum = sum + a [ p ] ; else { sum = sum + a [ p + 1 ] ; p = p + 1 ; } } printf ( "%ld\n" , sum ) ; } return 0 ; }