int main ( void ) { long long int t , n , i , k , s ; scanf ( "%lld" , & t ) ; while ( t -- ) { scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; s = n ; for ( i = 0 ; i < n - 1 ; i ++ ) { k = 0 ; while ( i < n - 1 && a [ i + 1 ] >= a [ i ] ) { k ++ ; i ++ ; } s += k * ( k + 1 ) / 2 ; } printf ( "%lld\n" , s ) ; } return 0 ; }