int main ( void ) { int x , y , t , a , b ; int gcd ( int x , int y ) ; int lcm ( int x , int y , int a ) ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & x , & y ) ; a = gcd ( x , y ) ; b = lcm ( x , y , a ) ; printf ( "%d\t%d\n" , a , b ) ; } return 0 ; } int gcd ( int x , int y ) { if ( y != 0 ) return gcd ( y , x % y ) ; else return x ; } int lcm ( int x , int y , int a ) { return ( ( x * y ) / a ) ; }